mapper.select:
select
api_id, api_name, api_url, api_method, pid, sort, description
from sardline.sardline_api


        and api_id = #{apiId}


        and api_name = #{apiName}


        and api_url = #{apiUrl}


        and api_method = #{apiMethod}


        and pid = #{pid}


        and sort = #{sort}


        and description = #{description}
